window.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelectorAll(".header__link");for(let o of e)o.addEventListener("click",(function(e){e.preventDefault(),t.classList.contains("_active-icon")&&(t.classList.remove("_active-icon"),c.classList.remove("_active"));const n=o.getAttribute("href");document.querySelector(n).scrollIntoView({behavior:"smooth",block:"start"})}));let t=document.querySelector(".menu__icon"),c=document.querySelector(".nav");t&&t.addEventListener("click",(()=>{t.classList.toggle("_active-icon"),c.classList.toggle("_active")}))}));